import React , {Component} from 'react';
// import {button} from 'react-bootstrap';
// import 'bootstrap/dist/css/bootstrap.min.css';
import './Toolbar.css';
import {Redirect} from 'react-router-dom';
import axios from 'axios';
import{Form,FormGroup,Row,Col} from 'react-bootstrap';
  import { ToastContainer, toast } from 'react-toastify';
  import 'react-toastify/dist/ReactToastify.css';
//   import Modal from 'react-bootstrap/Modal'
  import {Button} from 'react-bootstrap';
   import Modal from 'react-awesome-modal';
import { Container } from '@material-ui/core';
// import { Modal, Button } from 'antd'; 

class Toolbar extends Component
{
      constructor(props)
    {
        super(props)

        this.state={
            question:'' ,
            category:'' ,
            answer:'' ,
            visible: false
            //  items: []
        }
    }

    setRedirect=() =>
    {
        this.setState({
            redirect:true
        })
    }

    renderRedirect =() =>{
        if(this.state.redirect)
        {
            return<Redirect to='/faqdis'/>
        }

    }
         openModal=() =>
    {
      this.setState({ 
          visible : true,
            question:'' ,
            category:'' ,
            answer:'' ,

            });
    }


    closeModal = ()  => {
        this.setState({
            visible: false
        });
    }

    //  handleClose = () => setShow(false);
    //  handleShow = () => setShow(true);

       changeHandler= e =>{
        this.setState({[e.target.name]:e.target.value})
    }
    componentDidMount()
         {
      fetch('http://54e275d4.ngrok.io/faq/displayall')
       .then(res => res.json())
         .then(json => 
         {
           this.setState({
             isLoaded:true,
             items:json,
           })
         }) ;
         }

        submitHandler= e =>
    {
        e.preventDefault()
        console.log(this.state)
        axios
        .post('http://54e275d4.ngrok.io/faq/save',this.state)

        .then(response => {
            console.log(response)
            if(response.status == "200"){
                this.closeModal()
               window.location.reload()
              }
        })
        .catch(error => {
            console.log(error)
        })
        //  window.location.reload(e);
    }
      notify = () => toast("Wow so easy !");
      closeabc = () => {
          this.setState({show:false})
      }








   render()
   {

        const{question,category,answer}=this.state
       return(
           <div>
<header className="toolbar">                
    <nav className="toolbar_navigation">

    <div className="toolbar_logo"><img
                          src={require("/home/nineleaps/Desktop/Reactjs/Reactjs-devlopmentRJ/project1/src/Component/pages/ToolBar/nine1.jpg")}
                          width="150"
                          height="70"
                          className="d-inline-block align-top"
                          alt="React Bootstrap logo"
                          />
    </div>
            <div className="spacer"/>
            <div className="toolbar_navigation-items">
                <ul >
                    <li><a href='./table'>Checklist</a></li>
                    <li><a href='./join'>Users</a></li>
                    <li><a href='./displayblog'>Blog</a></li>
                    <li><a href='./faqdis'>FAQ</a></li>
                    <li><a href='/'>Logout</a></li>
                </ul>
            </div>
    </nav>
   <div className="actual">
     <button type="button" class="btn btn-primary" onClick={()=>this.openModal()}>ADD FAQ</button>    

</div>







</header>

 <div>


<div>

          <Modal title="Basic Modal" visible={this.state.visible} onOk={this.handleOk} onCancel={this.handleCancel} > 
          <p>Some contents...</p> <p>Some contents...</p> <p>Some contents...</p> 
          </Modal> </div> 

           <Modal visible={this.state.visible}  effect="fadeInUp" onClickAway={() => this.closeModal()} data-dismiss="modal" aria-label="Close">
           <Container style={{padding:"30px"}}>
           <Form>

           <Form.Group as={Row} controlId="formPlaintextPassword">
                    <Form.Label column sm="auto">
                    Question :
                    </Form.Label>
                    <Col >
                    <Form.Control value={this.state.question}
                onChange={this.changeHandler} type="text" name="question" placeholder="Question" required />
                    </Col>
                </Form.Group> 
                <Form.Group as={Row} controlId="formPlaintextPassword">
                    <Form.Label column sm="auto">
                    Category :
                    </Form.Label>
                    <Col >
                    <Form.Control value={this.state.category}
                onChange={this.changeHandler} type="text" name="category" placeholder="Category" required/>
                    </Col>
                </Form.Group> 
                <Form.Group controlId="exampleForm.ControlSelect2">
                    <Form.Label >Answer :</Form.Label>
                    <Form.Control value={this.state.answer}
                onChange={this.changeHandler} as="textarea" name="answer"  rows="3" />


                </Form.Group>
                <Button style={{marginLeft:"5rem"}} variant="primary" type="submit" onClick={this.submitHandler}>
                 Submit
                </Button>
                <Button style={{marginLeft:"3px"}} variant="primary" variant="dark" onClick={this.closeModal}>
                 Cancle
                </Button>
            </Form>
            </Container>


            </Modal> 
                                </div>
  </div>
       );
   }
}

export default Toolbar;
