    }

change = e => {
    // this.props.onChange({ [e.target.name]: e.target.value });
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  validate = () => {
    let isError = false;
    const errors = {
      document_idError: "",
      document_statusError: "",

    };

    this.setState({
      ...this.state,
      ...errors
    });

    return isError;
  };

  onSubmit = e => {
    e.preventDefault();
    const err = this.validate();
    if (!err) {
      this.props.onSubmit(this.state);
      //  this.props.handleSave(this.props.i, this.state.values);
      // clear form
      this.setState({
        document_name: "",
        document_nameError: "",
        document_status: false,
        document_statusError: "",

      });
    }
  };

  //     handleChangenew(checked) {
  //   this.setState({ checked });
  // } 

  // const body = {order,name,description};
  handleChange = idx => e => {
    //console.log('shravan:'+ typeof e);

    const { name, value } = e.target;
    // console.log("Target"+e.target)
    // console.log("Target Name"+[name])
    // console.log("Target Values"+[value])

    const rows = [...this.state.rows];
    rows[idx] = {
      [name]: value,

    };
    // console.log(rows)
    this.setState({rows});
  };

  handleAddRow = () => {
    const item = {
        order:'',
      name: '',
      description:''
    };
    // console.log("items:",item )
    //console.log("rows:"+this.state.rows)

    this.setState({
      rows: [...this.state.rows, item]

//      rows: [...this.state.rows, item.order,item.name,item.description]
    });   
  };

// this.setState(previousState => ({
//     myArray: [...previousState.myArray, 'new value']
// }));


  handleRemoveRow = () => {

    this.setState({
      rows: this.state.rows.slice(0, -1)
    });
  };
  handleRemoveSpecificRow = (idx) => () => {
    const rows = [...this.state.rows]
    rows.splice(idx, 1) //check
    // console.log("rows:"+rows)
    this.setState({ rows })
  }

openModal() {
this.setState({
visible : true,
checklist_name:'',
data: [{ order: 0, document_name: '', description: ''}],    
editIdx: -1,
columnToSort: "",
sortDirection: "desc",

status:false,
document_name:"",
document_status:false,

});
}

closeModal() {
this.setState({
visible : false
});
}

    changeHandler= e =>{


        this.setState({[e.target.name]:e.target.value})

    }


    // submitHandler= e =>
    // {

    //     // console.log('sree:'+ typeof e);
    //      console.log(this.state)
    //     e.preventDefault()

    //     // console.log("State Check:"+this.state.checkname)
    //     // console.log("State Rows:"+this.stataxios
        // .post('http://b9263691.ngrok.io/json',this.state)

        // .then(response => {
