    //     .then(response => {
    //         console.log(response)
    //     })
    //     .catch(error => {
    //         console.log(error)
    //     })
    // }


     setRedirect=() =>
    {
        window.location.reload();
    }


  // change = e => {
  //   // this.props.onChange({ [e.target.name]: e.target.value });
  //   this.setState({
  //     [e.target.name]: e.target.value
  //   });
  // };
  // changeHandler= e =>{
  //       this.setState({[e.target.name]:e.target.value})
  //   }
      submitHandler= e =>
    {

        e.preventDefault()
        console.log("check this one",this.state)
        axios
        .post('http://54e275d4.ngrok.io/json',this.state)

        .then(response => {
            console.log(response)
            if(response.status == "200"){
              this.closeModal()
            }
        })
        .catch(error => {
            console.log(error)
        })
    }


  handleRemove = i => {
    const { length } = this.state;
    this.setState(state => ({
      data: state.data.filter((row, j) => j !== i)
    }));
    if ( length > 0) this.setState({ length: this.state.length - 1});
  };

  startEditing = i => {
    this.setState({ editIdx: i });
  };

  stopEditing = () => {
    this.setState({ editIdx: -1 });
  };

  handleSave = (i, x) => {
    this.setState(state => ({
      data: state.data.map((row, j) => (j === i ? x : row))
    }));
    this.stopEditing();
  };

  handleSort = columnName => {
    this.setState(state => ({
      columnToSort: columnName,
      sortDirection:
        state.columnToSort === columnName
          ? invertDirection[state.sortDirection]
          : "asc"
    }));
  };




  render() 
  {   
  const{checklist_name}=this.state

    return (

      <div>
      <Header/>


            <div className="actualthree">
<input type="button" value="Create Checklist " class="btn btn-info" onClick={() => this.openModal()} />


            </div>




{/*popup model*/}
    <div>
      <MuiThemeProvider>
        <section>
          <Modal visible={this.state.visible} effect="fadeInUp" width="600" onClickAway={() => this.closeModal()}>   
          <div className="overflowone">

           <div className="App">
          {/* <form  onSubmit={this.submitHandler }> */}
          <Form  onSubmit={this.submitHandler}>
          <h1><center><strong>Create Checklist</strong></center></h1>

        <div className="Appa">
        <TextField
          name="checklist_name"

          floatingLabelText="Checklist Name"
          value={this.state.checklist_name}
          onChange={e => this.change(e)}

          floatingLabelFixed
        />
        <br/>
        <br/>
         {/* <label>Public/Private:</label>
                  <Switch
          onChange={this.handleChangenew}
          checked={this.state.checked}
          id="normal-switch"
        /> */}

       <Form.Group as={Row}>
      <Form.Label as="legend" row sm={10}>
       Status:
      </Form.Label>
      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
      <Row sm={30}>
        <Form.Check
          type="radio"
          label="Public"
          name="formHorizontalRadios"
          id="formHorizontalRadios1"
          onChange={()=>this.setState({status:false})}
        /> &nbsp;&nbsp;
        <Form.Check
          type="radio"
          label="Private"
          name="formHorizontalRadios"
          id="formHorizontalRadios2"
          onChange={()=>this.setState({status:true})}

        />
         {/* {console.log("status",this.state.status)} */}
      </Row>
    </Form.Group> 

     </div>




          <Table
            handleSort={this.handleSort}LLabelabel
            handleRemove={this.handleRemove}
            startEditing={this.startEditing}
            editIdx={this.state.editIdx}
            stopEditing={this.stopEditing}
            handleSave={this.handleSave}
            columnToSort={this.state.columnToSort}
            sortDirection={this.state.sortDirection}
            data={orderBy(
              this.state.data,
              this.state.columnToSort,
              this.state.sortDirection
            )}
            length={this.state.length}
            header={[
                {
                name: "Order",
                prop: "order"
              },
              {
                name: "Document Name",
                prop: "document_name"
              },


              {
                name: "Decsription",
                prop: "description"
              }

            ]}

          />
          &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
          <Formnew 
            onSubmit={submission =>
              this.setState({
                data: [...this.state.data, submission],
                length: this.state.length + 1,
              })
            }
          />

          <div className="actualthree">
          {/* <input type="submit" class="btn btn-primary" value="Save"/> &nbsp;  &nbsp;  */}
          <Button type="submit">Save</Button>&nbsp;  &nbsp;
          <Button onClick={()=>this.closeModal()} class="btn btn-info">Cancel</Button> 
            <br/>   
            </div>

          {/* </form> */}
</Form>

        </div>





          </div>             
          </Modal>
        </section>
      </MuiThemeProvider>

    </div>     
</div> 
    );

