import React,{Component} from 'react';
 import axios from 'axios';
import './Toolbar.css';
import Toolbar from './Toolbar.js';
import {Button,Accordion,Card,ButtonGroup} from 'react-bootstrap';

class PostList extends Component




{ constructor(props)
    {
        super(props);

        this.state=
        {
           items:[],
           isLoaded:false,
        }
    }
    componentDidMount()
         {
      fetch('http://54e275d4.ngrok.io/faq/displayall')
       .then(res => res.json())
         .then(json => 
         {
           this.setState({
             isLoaded:true,
             items:json,
           })
         }) ;
         }

         openModal=() =>
    {
      this.setState({ 
        visible : true 
         });
    }




    closeModal = ()  => {
        this.setState({
            visible: false
        });
    }

    deleteitems=(id)=>
{   
  // this.setState({ id: id})
  console.log(id)

      axios.delete('http://54e275d4.ngrok.io/faq/delete/'+id).then(response =>{
        console.log(response)
        if(response.status == "200"){
          window.location.reload();
        }
      })
    // { data: { id:id } }

//window.location.reload();
}

render()
 {
   var  {isLoaded,items}=this.state;

   if(!isLoaded)
   {
     return <div>Loading....</div>
   }
   else
   { return(
     <div >

       <Toolbar/>



         <div style={{width:"80%",marginLeft:"10rem", marginTop:"10rem"}}>   
          {items.map((item,i) =>(
            <Accordion style={{height:"auto"}} defaultActiveKey="1">
                  <Card>
                    <Accordion.Toggle style={{backgroundColor:"#566787"}} as={Card.Header} eventKey="0">
                    <div>
                      <text style={{color:'white'}}>{item.question }</text>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;


                       <Button style={{align:'right', height:'25px',}} size="sm" variant="danger" onClick={()=>this.deleteitems(item.id)}>Delete</Button>

                      </div>




                    </Accordion.Toggle>
                    <Accordion.Collapse eventKey="0">
                      <Card.Body>{item.answer}</Card.Body>
                    </Accordion.Collapse>
                  </Card>
                  </Accordion>
             ))}
          </div>

     </div>
   );
   }
 }
}
export default PostList;
